    private void MAKKA_INFMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_MAKKA_INFMouseClicked
        try {
            Boolean pp = MAKKA_IN.print();
            if(pp)
            {
                JOptionPane.showMessageDialog(null,"Done");
                dispose();
            }
        } catch (PrinterException ex) {
            Logger.getLogger(MAKKA_INFO.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_MAKKA_INFMouseClicked

